INSERT INTO `book` VALUES (1376930620990685184, '聊斋志异', 'http://image12.bookschina.com/2013/20131114/5888867.jpg', 18.00, 999, 16.00, 0, 890, 18.00, '<h2 class=\"tit\"><span>聊斋志异 内容简介</span></h2> \n<p> 　　《中国古典文学名著丛书：聊斋志异》题材非常广泛，内容极其丰富，艺术成就很高。作品成功地塑造了众多的艺术典型，人物形象鲜明生动，故事情节曲折离奇，结构布局严谨巧妙，文笔简练，描写细腻，堪称中国古典文言短篇小说之巅峰。 </p>', '', '', '<h2 class=\"tit\"><span>聊斋志异 目录</span></h2> \n<div class=\"con\"> \n <div id=\"catalogSwitch\"> \n  <br>卷一\n  <br>考城隍\n  <br>耳中人\n  <br>尸变\n  <br>喷水\n  <br>瞳人语\n  <br>画壁\n  <br>山魈\n  <br>咬鬼\n  <br>捉狐\n  <br>荍中怪\n  <br>宅妖\n  <br>王六郎\n  <br>偷桃\n  <br>种梨\n  <br>劳山道士\n  <br>长清僧\n  <br>蛇人\n  <br>斫蟒\n  <br>灾奸\n  <br>雹神\n  <br>狐嫁女\n  <br>娇‘娜\n  <br>僧孽\n  <br>妖术\n  <br>野狗\n  <br>三生\n  <br>狐入瓶\n  <br>鬼哭\n  <br>真定女\n  <br>焦螟\n  <br>叶生\n  <br>四十千\n  <br>成仙\n  <br>新郎\n  <br>灵官\n  <br>王兰\n  <br>鹰虎神\n  <br>王成\n  <br>青凤\n  <br>画皮\n  <br>贾儿\n  <br>蛇癖\n  <br>\n  <br>卷二\n  <br>金世成\n  <br>董生\n  <br>龅石\n  <br>庙鬼\n  <br>陆判\n  <br>婴宁\n  <br>聂小倩\n  <br>义鼠\n  <br>地震\n  <br>海公子\n  <br>丁前溪\n  <br>海大鱼\n  <br>张老相公\n  <br>水莽草\n  <br>造畜\n  <br>凤阳士人\n  <br>耿十八\n  <br>珠儿\n  <br>小官人\n  <br>胡四姐\n  <br>祝翁\n  <br>猪婆龙\n  <br>某公\n  <br>快刀\n  <br>侠女\n  <br>酒友\n  <br>莲香\n  <br>阿宝\n  <br>九山王\n  <br>遵化署狐\n  <br>张诚\n  <br>汾州狐\n  <br>巧娘\n  <br>吴令\n  <br>口技\n  <br>狐联\n  <br>潍水狐\n  <br>红玉\n  <br>龙\n  <br>林四娘\n  <br>\n  <br>卷三\n  <br>江中\n  <br>鲁公女\n  <br>道士\n  <br>胡氏\n  <br>戏术\n  <br>丐僧\n  <br>伏狐\n  <br>蛰龙\n  <br>苏仙\n  <br>李伯言\n  <br>黄九郎\n  <br>金陵女子\n  <br>汤公\n  <br>阎罗\n  <br>连琐\n  <br>单道士\n  <br>白于玉\n  <br>夜叉国\n  <br>小髻\n  <br>西僧\n  <br>老饕\n  <br>连城\n  <br>霍生\n  <br>汪士秀\n  <br>商三官\n  <br>于江\n  <br>小二\n  <br>庚娘\n  <br>宫梦弼\n  <br>鸲鹆\n  <br>刘海石\n  <br>谕鬼\n  <br>泥鬼\n  <br>梦别\n  <br>犬灯\n  <br>番僧\n  <br>狐妾\n  <br>雷曹\n  <br>赌符\n  <br>阿霞\n  <br>李司鉴\n  <br>五羖大夫\n  <br>毛狐\n  <br>翩翩\n  <br>黑兽\n  <br>\n  <br>卷四\n  <br>余德\n  <br>杨千总\n  <br>瓜异\n  <br>青梅\n  <br>罗刹海市\n  <br>田七郎\n  <br>产龙\n  <br>保住\n  <br>公孙九娘\n  <br>促织\n  <br>柳秀才\n  <br>水灾\n  <br>诸城某甲\n  <br>库官\n  <br>酆都御史\n  <br>龙无目\n  <br>狐谐\n  <br>雨钱\n