-- ----------------------------
-- Table structure for book
-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `id` bigint(20) NOT NULL COMMENT '主键',
  `name` varchar(100) NOT NULL COMMENT '书名',
  `pic_url` varchar(200) NOT NULL DEFAULT '' COMMENT '封面',
  `purchase_price` decimal(5,2) NOT NULL COMMENT '进货价格',
  `purchase_num` int(11) NOT NULL DEFAULT '999' COMMENT '进货数量',
  `selling_price` decimal(5,2) NOT NULL COMMENT '售价',
  `sales_volume` int(11) NOT NULL DEFAULT '0' COMMENT '销售量',
  `discount` int(4) DEFAULT '1000' COMMENT '折扣: discount / 1000为实际折扣，discount / 100为显示的折扣',
  `original_price` decimal(5,2) NOT NULL COMMENT '原价',
  `brief` text NOT NULL COMMENT '内容简介',
  `preface` text NOT NULL COMMENT '前言',
  `excerpt` text NOT NULL COMMENT '节选',
  `catalog` varchar(5000) NOT NULL DEFAULT '' COMMENT '目录',
  `author` varchar(500) NOT NULL DEFAULT '' COMMENT '作者',
  `publisher` varchar(500) NOT NULL DEFAULT '' COMMENT '出版社',
  `other_infor` varchar(200) NOT NULL DEFAULT '' COMMENT '其他信息',
  `classification` int(4) DEFAULT NULL,
  `category` int(4) DEFAULT NULL,
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态: 0: 已删除, 1: 已上架: 2: 未上架',
  `sort` int(10) NOT NULL DEFAULT '1' COMMENT '排序',
  `gmt_create` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `gmt_modified` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `idx_name` (`name`) USING BTREE,
  KEY `idx_classification` (`classification`) USING BTREE,
  KEY `idx_status` (`status`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='书籍表';

-- ----------------------------
-- Records of book
-- ----------------------------
BEGIN;
COMMIT;

