  `id` bigint(20) NOT NULL COMMENT '主键',
  `order_no` bigint(20) NOT NULL COMMENT '订单号',
  `book_id` bigint(20) NOT NULL COMMENT '书籍主键',
  `user_id` bigint(20) NOT NULL COMMENT '用户主键',
  `quantity` int(4) NOT NULL COMMENT '商品数量',
