-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` bigint(20) NOT NULL COMMENT '主键',
  `open_id` varchar(200) NOT NULL DEFAULT '' COMMENT '长期授权字符串',
  `nickname` varchar(32) NOT NULL COMMENT '昵称',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '姓名',
  `sex` enum('男','女','保密') NOT NULL DEFAULT '保密' COMMENT '性别',
  `phone` char(11) NOT NULL DEFAULT '' COMMENT '手机号',
  `password` varchar(256) NOT NULL DEFAULT '' COMMENT '密码，暂未定哪种方式登录',
  `head_portrait` varchar(255) NOT NULL DEFAULT '' COMMENT '用户头像，小头像',
  `head_portrait_big` varchar(255) NOT NULL DEFAULT '' COMMENT '用户头像，大头像',
  `province` varchar(32) NOT NULL DEFAULT '' COMMENT '省份',
  `city` varchar(32) NOT NULL DEFAULT '' COMMENT '城市',
  `district` varchar(32) NOT NULL DEFAULT '' COMMENT '区县',
  `birthday` date NOT NULL DEFAULT '1970-01-01' COMMENT '生日',
  `role` json DEFAULT NULL COMMENT '角色',
  `root` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否是超级管理员',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态: 0: 已删除, 1: 已激活, 2: 已冻结',
  `gmt_create` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `gmt_modified` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `unq_open_id` (`open_id`) USING BTREE,
  KEY `idx_status` (`status`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户表';

